
document.addEventListener('DOMContentLoaded', () => {

    const bookCovers = document.querySelectorAll('.book-cover');
  
    bookCovers.forEach(cover => {
      const bookImage = cover.querySelector('.img-fluid'); 

      if (bookImage) {
        bookImage.addEventListener('mouseover', () => {
          const bookActions = cover.querySelector('.book-actions');
          bookActions.style.display = 'flex';
        });
  
        bookImage.addEventListener('mouseout', () => {
          const bookActions = cover.querySelector('.book-actions');
          bookActions.style.display = 'none';
        });
      } else {

        console.warn('Book image element not found!');
      }
  
      bookActions.querySelectorAll('button').forEach(button => {
        button.addEventListener('click', () => {
          alert('¡Has hecho clic en un botón del libro!');
        });
      });
    });
  });